mapper.select:
SELECT z.* FROM (SELECT ROWNUM as recNum, a.* FROM (SELECT b.article_no, b.user_id, u.nickname, b.group_id, b.create_date, b.title, b.brackets, b.view_cnt, b.comment_cnt FROM BOARD_TBL b, USER_TBL u WHERE u.user_id=b.user_id AND b.group_id=#{group_id}
AND b.brackets=#{keyword}
AND b.title LIKE '%'||#{keyword}||'%'
AND b.content LIKE '%'||#{keyword}||'%'
AND u.nickname LIKE '%'||#{keyword}||'%'
order by b.article_no desc) a ) z WHERE recNum BETWEEN (#{section}-1)*100 + (#{pagenum}-1)*10+1 AND (#{section}-1)*100 + #{pagenum}*10

SELECT count(*) FROM (SELECT ROWNUM as recNum, a.* FROM (SELECT b.article_no, b.user_id, u.nickname, b.group_id, b.create_date, b.title, b.brackets, b.view_cnt, b.comment_cnt FROM BOARD_TBL b, USER_TBL u WHERE u.user_id=b.user_id AND b.group_id=#{group_id}
AND b.brackets=#{keyword}
AND b.title LIKE '%'||#{keyword}||'%'
AND b.content LIKE '%'||#{keyword}||'%'
AND u.nickname LIKE '%'||#{keyword}||'%'
order by b.article_no desc) a ) z WHERE recNum BETWEEN (#{section}-1)*100 + (#{pagenum}-1)*10+1 AND (#{section}-1)*100 + #{pagenum}*10
